mapper.update:
UPDATE MEMBER
SET
	mem_name         = #{mem_name}
	mem_zip          = #{mem_zip}
	mem_add1         = #{mem_add1}
	mem_add2         = #{mem_add2}
	mem_hometel      = #{mem_hometel}
	mem_comtel       = #{mem_comtel}
	mem_hp           = #{mem_hp,jdbcType=VARCHAR}
	mem_mail         = #{mem_mail}
	mem_job          = #{mem_job,jdbcType=VARCHAR}
	mem_like         = #{mem_like,jdbcType=VARCHAR}
	mem_memorial     = #{mem_memorial,jdbcType=VARCHAR}
	mem_memorialday  = TO_DATE(#{mem_memorialday,jdbcType=DATE}, 'YYYY-MM-DD')
WHERE mem_id = #{mem_id}

mapper.delete:
UPDATE MEMBER
SET MEM_DELETE = 'Y'
WHERE MEM_ID = #{mem_id}
